import Container from "../../Atoms/Container/container.view";
import Label from "../../Atoms/Label/label.view";
import { useNavigate } from "react-router-dom";
import { StyledAuthForm } from "./authform.styles";
import { AuthFormButton } from "./components/AuthFormButton/authformbutton.styles";
import { AuthFormInput } from "./components/AuthFormInput/authformlabel.styles";

export default function AuthForm() {

  const userkey = (document.getElementById('userkey') as HTMLInputElement)
  const navigate = useNavigate()

  const salvaKey = () => {
    if (userkey.value !== '') {

      localStorage.setItem('userkey', userkey.value)

      navigate('/clientes')
    }
    else {
      alert('Você precisa preencher com uma User-Key válida para utilizar o site!')
    }
  }

  return (
    <StyledAuthForm>
      <Container>
        <Label>Para acessar a página preencha com uma Userkey válida de um Usuário de Integração</Label>
        <AuthFormInput
          type={""}
          placeholder={"Insira sua User-Key aqui..."}
          id={"userkey"}
          required
        />
        <AuthFormButton
          type="submit"
          onClick={salvaKey}
        >
          Enviar
        </AuthFormButton>
      </Container>
    </StyledAuthForm>
  )
}
