import styled from 'styled-components';

export const StyledTableRow = styled.tr`
  width: 100%;
  height: 60px;
  cursor: pointer;
  transition: 0.5s;
  border: none;

  &:hover {
    background-color: ${props => props.theme.pallete.primary.grey};
  }

  &:active {
    background-color: ${props => props.theme.pallete.primary.black};
  }
`
