import React, { useState } from 'react';

import Container from "../../Atoms/Container/container.view";
import Label from "../../Atoms/Label/label.view";
import { useNavigate } from "react-router-dom";
import { StyledAuthForm } from "./authform.styles";
import { AuthFormButton } from "./components/AuthFormButton/authformbutton.styles";
import { AuthFormInput } from "./components/AuthFormInput/authformlabel.styles";

export default function AuthForm() {
  const [userkey, setUserkey] = useState('');

  const navigate = useNavigate()

  let debounceTimer: ReturnType<typeof setTimeout>;

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    setUserkey(newValue);

    // Limpa o timer anterior e configura um novo
    if (debounceTimer) {
      clearTimeout(debounceTimer);
    }

    debounceTimer = setTimeout(() => {
      setUserKey(newValue);
    }, 500);
  };

  // const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
  //   setUserkey(e.target.value)
  // }

  const salvaKey = () => {
      localStorage.setItem('userkey', userkey)

      //navigate('contacts')
  }

  return (
    <StyledAuthForm>
      <Container>
        <Label>Para acessar a página preencha com uma Userkey válida de um Usuário de Integração</Label>
        <AuthFormInput
          type={""}
          placeholder={"Insira sua User-Key aqui..."}
          id={"userkey"}
          required
          value={userkey}
          onChange={handleInputChange}
        />
        <AuthFormButton
          type="submit"
          onClick={salvaKey}
        >
          Enviar
        </AuthFormButton>
      </Container>
    </StyledAuthForm>
  )
}
