import React, { useState } from 'react';
import Container from "../../Atoms/Container/container.view";
import Label from "../../Atoms/Label/label.view";
import { useNavigate } from "react-router-dom";
import { StyledAuthForm } from "./authform.styles";
import { AuthFormButton } from "./components/AuthFormButton/authformbutton.styles";
import { AuthFormInput } from "./components/AuthFormInput/authformlabel.styles";

export default function AuthForm() {
  const [userkey, setUserkey] = useState('');
  const navigate = useNavigate();
  
  function debounce(func: Function, delay: number) {
    let timeout: ReturnType<typeof setTimeout>;

    return function (...args: any[]) {
      const context = this;
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(context, args), delay);
    };
  }

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    debouncedSave(newValue);
  };

  const debouncedSave = debounce((newValue: string) => {
    setUserkey(newValue);
  }, 300);

  function salvaKey() {
    localStorage.setItem('userkey', userkey);

    navigate('contacts');
  }

  return (
    <StyledAuthForm>
      <Container>
        <Label>Para acessar a página preencha com uma Userkey válida de um Usuário de Integração</Label>
        <AuthFormInput
          type={""}
          placeholder={"Insira sua User-Key aqui..."}
          id={"userkey"}
          required
          value={userkey}
          onChange={handleInputChange}
        />
        <AuthFormButton
          type="submit"
          onClick={salvaKey}
        >
          Enviar
        </AuthFormButton>
      </Container>
    </StyledAuthForm>
  )
}
