import styled from 'styled-components'

export const StyledInput = styled.input`

    border: none;
    border-radius: 4px;
    padding: 8px;
    margin: 4px;
    height: 32px;
    overflow-x: auto
    max-width: 100%;
  max-height: 100%;
  overflow: auto;
  position: fixed;;
    width: ${props => props.width ? props.width : 'auto'};

    &:active {
        text-decoration: none;
    }
`;
