import Container from "../../Atoms/Container/container.view";
import React, { useState } from 'react';
import { useDebouncedCallback } from 'use-debounce';
import Label from "../../Atoms/Label/label.view";
import { useNavigate } from "react-router-dom";
import { StyledAuthForm } from "./authform.styles";
import { AuthFormButton } from "./components/AuthFormButton/authformbutton.styles";
import { AuthFormInput } from "./components/AuthFormInput/authformlabel.styles";

export default function AuthForm() {
  const [userkey, setUserkey] = useState('');
  

  const navigate = useNavigate()

	const handleChange = event => {
		const { value: nextValue } = event.target;
		setValue(nextValue);
		// Início do destaque
		const debouncedSave = debounce(() => saveToDb(nextValue), 1000);
		debouncedSave();
		// Fim do destaque
	};

  const salvaKey = () => {
      localStorage.setItem('userkey', userkey)

      //navigate('contacts')
  }

  return (
    <StyledAuthForm>
      <Container>
        <Label>Para acessar a página preencha com uma Userkey válida de um Usuário de Integração</Label>
        <AuthFormInput
          type={""}
          placeholder={"Insira sua User-Key aqui..."}
          id={"userkey"}
          required
          value={userkey}
          onChange={handleChange}
        />
        <AuthFormButton
          type="submit"
          onClick={salvaKey}
        >
          Enviar
        </AuthFormButton>
      </Container>
    </StyledAuthForm>
  )
}
